import React, { useState } from 'react'
import withRedux from 'next-redux-wrapper'
import Layout from '../components/MyLayout.js'
import Button from '../components/Button'
import Link from 'next/link'
// import makeStore from '../store/index'

function getPosts() {
  return [
    {
      id: 'hello-nextjs',
      title: 'Hello Next.js',
    },
    {
      id: 'learn-nextjs',
      title: 'Learn Next.js is awesome',
    },
    {
      id: 'deploy-nextjs',
      title: 'Deploy apps with ZEIT',
    },
  ]
}

const PostLink = ({ post }) => (
  <li>
    <Link href="/p/[id]" as={`/p/${post.id}`}>
      <a>{post.title}</a>
    </Link>
    <style jsx>{`
      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
        font-family: 'Arial';
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </li>
)

function Blog() {
  let [count, setCount] = useState(0)

  const handleCount = () => {
    setCount((count += 1))
  }

  return (
    <Layout>
      <h1>My Blog</h1>
      <h3>My count: {count}</h3>
      <ul>
        {getPosts().map(post => (
          <PostLink key={post.id} post={post} />
        ))}
      </ul>
      <br />
      <Button name="Counter" onClick={handleCount} />
      <style jsx>{`
        h1,
        a {
          font-family: 'Arial';
        }

        ul {
          padding: 0;
        }

        li {
          list-style: none;
          margin: 5px 0;
        }

        a {
          text-decoration: none;
          color: blue;
        }

        a:hover {
          opacity: 0.6;
        }
      `}</style>
    </Layout>
  )
}
export default Blog

